declare namespace common{
  // 定位
  export type position='static'|'position'|'relative'|'fixed'|'sticky'
  // text-align
  export type textAlign='left'|'center'|'right'|'justify'
  // display
  export type display='inline'|'block'|'inline-block'|'flex'
  // vertical-align
  export type vertical='baseline'|'sub'|'sup'|'top'|'text-top'|'middle'|'bottom'|'text-bottom'
  // overflow
  export type overflow='auto'|'scroll'|'hidden'|'visible'
  // text-decoration
  export type decoration='none'|'underline'|'overline'|'line-through'
  // word-break
  export type wordBreak='normal'|'break-all'|'keep-all'
}