declare namespace row{
  // 方向
  export type dir='left'|'right'|'top'|'bottom'
  // 水平对齐
  export type justify='flex-start'|'center'|'flex-end'|'space-between'|'space-around'
  // 垂直对齐
  export type align='flex-start'|'center'|'flex-end'|'baseline'|'stretch'
  // 段落堆叠
  export type wrap='nowrap'|'wrap'|'warp-reverse'
}